import React, { useState } from 'react';

const WeatherApp = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = process.env.REACT_APP_WEATHER_API_KEY;

  const fetchWeather = async () => {
    if (!city) return;

    try {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(city)},AR&appid=${API_KEY}&units=metric&lang=es`;
      console.log("URL generada:", url); // Para verificar en consola

      const response = await fetch(url);
      const data = await response.json();

      if (response.ok) {
        setWeather(data);
        setError('');
      } else {
        setWeather(null);
        setError(data.message || 'Ciudad no encontrada');
      }
    } catch (err) {
      setError('Error al obtener los datos');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>üå§Ô∏è Consulta el Clima</h1>
      <input
        type="text"
        placeholder="Escrib√≠ una ciudad..."
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ padding: '10px', width: '60%', marginBottom: '10px' }}
      />
      <br />
      <button onClick={fetchWeather} style={{ padding: '10px 20px' }}>
        Buscar
      </button>

      {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: '20px' }}>
          <h2>{weather.name}, {weather.sys.country}</h2>
          <p>üå°Ô∏è Temperatura: {weather.main.temp}¬∞C</p>
          <p>üå•Ô∏è Condici√≥n: {weather.weather[0].description}</p>
          <p>üíß Humedad: {weather.main.humidity}%</p>
          <p>üå¨Ô∏è Viento: {weather.wind.speed} m/s</p>
        </div>
      )}
    </div>
  );
};

export default WeatherApp;
